import React from 'react';
import './Register.css';

class Register extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isTeamNameAvailable: false,
      teamName:'',
      items:'<table><th>Candidate 1</th><th>Candidate 2</th>'+
            '<tr><td>'+'<table><th></th>'+
            '<tr><td><div class=\'\'>Name</div><input type=\'text\'></td></tr>'+
            '<tr><td><div>Phone Number</div><input type=\'text\'></td></tr>'+
            '<tr><td><div>Email ID</div><input type=\'text\'></td></tr>'+
            '</table></td></tr>'+
            '</table>'
    };
    this.checkTeamAvailable = this.checkTeamAvailable.bind(this);
    this.handleTeamChange = this.handleTeamChange.bind(this);
}

handleTeamChange(event){
    this.setState({teamName:event.target.value});
}

checkTeamAvailable(){
    this.setState({isTeamNameAvailable: true});
    fetch("http://localhost:8080/codingPlatform/checkTeam?key=SHARED_KEY",{
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      method: "POST",
      body: JSON.stringify({teamName:this.state.teamName})
    })
      .then(res => res.json())
      .then(
        (result) => {
          if(result.status === "Fail"){
            this.setState({isTeamNameAvailable:true});
          }
        }
      )
  }

render() {
  return (
    <div className="header">
        <div className="headerBackground">
        <div className="heading">Break the ENGIMA</div>
        <div className="quote">“Those who can imagine anything, can create the impossible.”
            ― Alan Turing</div>
        </div>
        <div className="registerForm">
            <div className="teamName">Enter Team Name</div>
            <div className="teamNameDiv"><input type="text" className="teamNameInput" onChange={this.handleTeamChange}/></div>
            <div className="submitDiv"><div className="submitButton" onClick={this.checkTeamAvailable}>Check Validity</div></div>
        </div>
        {this.state.isTeamNameAvailable === true?
        <div className="registerForm">
            <div dangerouslySetInnerHTML={{ __html: this.state.items }} />
        </div>:<div></div>}
    </div>
  );
}
}
export default Register;
